 int(11) NOT NULL AUTO_INCREMENT,  `origin_name` varchar(255) NOT NULL,  `name` varchar(255) NOT NULL,  `operator` varchar(100) DEFAULT NULL,  `operate_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  `md5` varchar(40) NOT NULL,  `size` bigint(20) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `investment`-- ----------------------------DROP TABLE IF EXISTS `investment`;CREATE TABLE `investment` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `username` varchar(20) NOT NULL,  `mobile` varchar(30) NOT NULL,  `telephone` varchar(30) DEFAULT NULL,  `fax` varchar(30) DEFAULT NULL,  `area` varchar(11) NOT NULL DEFAULT '-1' COMMENT '所在地区',  `sex` varchar(1) NOT NULL DEFAULT 'M',  `mail` varchar(50) DEFAULT NULL,  `qq` varchar(20) DEFAULT NULL,  `business_brand` varchar(1000) DEFAULT NULL COMMENT '经营品牌',  `business_model` varchar(11) NOT NULL DEFAULT '-1' COMMENT '经营模式',  `venue_name` varchar(200) DEFAULT NULL COMMENT '场馆名称',  `business_size` varchar(20) DEFAULT NULL COMMENT '经营面积',  `contract_time` varchar(50) DEFAULT NULL COMMENT '合同到期时间',  `investment_amount` int(11) DEFAULT NULL COMMENT '投资金额',  `comment` text COMMENT '备注',  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',  `status` int(11) NOT NULL DEFAULT '1',  `operator` varchar(50) DEFAULT NULL,  `operate_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `product`-- ----------------------------DROP TABLE IF EXISTS `product`;CREATE TABLE `product` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `title` varchar(100) NOT NULL,  `image` varchar(100) NOT NULL,  `status` int(20) DEFAULT '1',  `create_time` datetime DEFAULT CURRENT_TIMESTAMP,  `update_time` datetime DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `schedule_execute_result`-- ----------------------------DROP TABLE IF EXISTS `schedule_execute_result`;CREATE TABLE `schedule_execute_result` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `schedule_id` varchar(200) NOT NULL,  `start_time` datetime NOT NULL,  `end_time` datetime DEFAULT NULL,  `status` int(11) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `schedule_job_setting`-- ----------------------------DROP TABLE IF EXISTS `schedule_job_setting`;CREATE TABLE `schedule_job_setting` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `schedule_id` varchar(200) NOT NULL,  `group_id` varchar(100) NOT NULL,  `cron` varchar(100) DEFAULT NULL,  `class_path` varchar(200) NOT NULL,  `status` int(11) NOT NULL,  PRIMARY KEY (`id`),  KEY `schedule_id` (`schedule_id`,`group_id`) USING BTREE,  KEY `class_path` (`class_path`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `short_url`-- ----------------------------DROP TABLE IF EXISTS `short_url`;CREATE TABLE `short_url` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `origin` varchar(1000) NOT NULL,  `current` varchar(100) NOT NULL,  `status` int(11) DEFAULT '1',  `create_time` datetime DEFAULT CURRENT_TIMESTAMP,  `invalid_time` datetime DEFAULT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `idx_current` (`current`) USING BTREE) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `sys_acl`-- ----------------------------DROP TABLE IF EXISTS `sys_acl`;CREATE TABLE `sys_acl` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `code` varchar(40) NOT NULL,  `name` varchar(20) NOT NULL,  `acl_module_id` int(11) NOT NULL,  `url` varchar(200) DEFAULT NULL,  `type` int(11) NOT NULL,  `status` int(11) NOT NULL,  `seq` int(11) NOT NULL,  `remark` varchar(100) DEFAULT NULL,  `operator` varchar(50) NOT NULL,  `operate_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,  `operate_ip` varchar(20) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `sys_acl_module`-- ----------------------------DROP TABLE IF EXISTS `sys_acl_module`;CREATE TABLE `sys_acl_module` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(255) NOT NULL,  `parent_id` int(11) DEFAULT NULL,  `level` varchar(1000) NOT NULL,  `status` int(11) NOT NULL,  `seq` int(11) NOT NULL,  `remark` varchar(100) DEFAULT NULL,  `operator` varchar(50) NOT NULL,  `operate_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,  `operate_ip` varchar(20) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `sys_dept`-- ----------------------------DROP TABLE IF EXISTS `sys_dept`;CREATE TABLE `sys_dept` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(20) NOT NULL,  `supplier_id` int(11) NOT NULL,  `level` varchar(1000) NOT NULL DEFAULT '',  `seq` int(11) NOT NULL,  `remark` varchar(100) DEFAULT NULL,  `parent_id` int(11) DEFAULT NULL,  `operator` varchar(50) NOT NULL,  `operate_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,  `operate_ip` varchar(20) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `sys_log`-- ----------------------------DROP TABLE IF EXISTS `sys_log`;CREATE TABLE `sys_log` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `type` int(11) NOT NULL,  `target_id` int(11) NOT NULL,  `old_value` text,  `new_value` text,  `operator` varchar(50) NOT NULL,  `operate_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,  `operate_ip` varchar(20) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=97 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `sys_role`-- ----------------------------DROP TABLE IF EXISTS `sys_role`;CREATE TABLE `sys_role` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(20) NOT NULL,  `supplier_id` int(11) NOT NULL,  `type` int(11) NOT NULL,  `status` int(11) NOT NULL,  `remark` varchar(255) DEFAULT NULL,  `operator` varchar(50) NOT NULL,  `operate_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,  `operate_ip` varchar(20) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `sys_role_acl`-- ----------------------------DROP TABLE IF EXISTS `sys_role_acl`;CREATE TABLE `sys_role_acl` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `role_id` int(11) NOT NULL,  `acl_id` int(11) NOT NULL,  `operator` varchar(50) NOT NULL,  `operate_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,  `operate_ip` varchar(20) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `sys_role_user`-- ----------------------------DROP TABLE IF EXISTS `sys_role_user`;CREATE TABLE `sys_role_user` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `role_id` int(11) NOT NULL,  `user_id` int(11) NOT NULL,  `operator` varchar(50) NOT NULL,  `operate_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,  `operate_ip` varchar(20) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;-- ------------------------------  Table structure for `sys_user`-- ----------------------------DROP TABLE IF EXISTS `sys_user`;CREATE TABLE `sys_user` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `username` varchar(50) NOT NULL,  `telephone` varchar(30) DEFAULT NULL,  `mail` varchar(50) DEFAULT NULL,  `password` varchar(32) DEFAULT NULL,  `remark` varchar(1000) DEFAULT NULL,  `dept_id` int(11) NOT NULL,  `status` int(11) NOT NULL,  `supplier_id` int(11) NOT NULL,  `managed_supplier_ids` text,  `operator` varchar(50) NOT NULL,  `operate_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,  `operate_ip` varchar(20) NOT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `idx_username` (`username`) USING BTREE,  UNIQUE KEY `idx_mail` (`mail`) USING BTREE) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;SET FOREIGN_KEY_CHECKS = 1;